mapper.select:

SELECT *
FROM (SELECT ROWNUM AS RNUM, P.*
	 FROM ( SELECT * 
	 		FROM ANIMAL_SHELTER 

					WHERE upper(CENTERNAME) like '%' || upper(#{txt_search}) || '%'

				ORDER BY AREA
				) P
	 )
WHERE RNUM BETWEEN #{firstIndex} +1 AND #{firstIndex} +	#{recordCountPerPage}


SELECT *
FROM (SELECT ROWNUM AS RNUM, P.*
	 FROM ( SELECT * 
	 		FROM ANIMAL_ENDANGERED 

					WHERE upper(CENTERNAME) like '%' || upper(#{txt_search}) || '%'

				ORDER BY AREA
				) P
	 )
WHERE RNUM BETWEEN #{firstIndex} +1 AND #{firstIndex} +	#{recordCountPerPage}
