mapper.select:
SELECT * FROM (SELECT ROWNUM AS RNUM, P.* FROM ( SELECT * FROM ANIMAL_SHELTER
WHERE upper(AREA) like '%' || upper(#{txt_search}) || '%' or upper(CENTERNAME) like '%' || upper(#{txt_search}) || '%' or upper(CENTERADDR) like '%' || upper(#{txt_search}) || '%'
ORDER BY AREA ) P ) WHERE RNUM BETWEEN #{firstIndex} +1 AND #{firstIndex} + #{recordCountPerPage}

SELECT GROUPS, GRADE, KOR_NAME, ENG_NAME, KOR_RED_LIST, ENG_RED_LIST, IMG FROM (SELECT ROWNUM AS RNUM, P.* FROM ( SELECT * FROM ANIMAL_ENDANGERED_CSV JOIN ANIMAL_ENDANGERED_IMG USING(KOR_NAME)
WHERE upper(KOR_NAME) like '%' || upper(#{txt_search}) || '%' or upper(ENG_NAME) like '%' || upper(#{txt_search}) || '%'
ORDER BY GROUPS, KOR_NAME ) P ) WHERE RNUM BETWEEN #{firstIndex} +1 AND #{firstIndex} + #{recordCountPerPage}
